import React, { useState, useEffect } from 'react';

function ChatBox() {
  const [userMessage, setUserMessage] = useState('');
  const [messages, setMessages] = useState([]);

  const sendMessage = () => {
    const query = new URLSearchParams({ message: userMessage }).toString();
    const url = `http://localhost:5000/chat?${query}`;
    const eventSource = new EventSource(url);

    eventSource.onmessage = function(event) {
      console.log('New message from API:', event.data);  // Ensure this logs
      setMessages(prevMessages => [...prevMessages, { role: 'bot', content: event.data }]);
    };

    eventSource.onerror = function(error) {
      console.error('EventSource failed:', error);
      eventSource.close();
    };

    setUserMessage('');  // Clear input after sending
  };

  // This return statement should be inside the function body of ChatBox
  return (
    <div>
      <h1>Chat with Bot</h1>
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.role === 'user' ? 'user' : 'bot'}>
            <strong>{msg.role === 'user' ? 'You' : 'Bot'}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={userMessage}
        onChange={e => setUserMessage(e.target.value)}
        onKeyPress={e => e.key === 'Enter' && sendMessage()}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}

export default ChatBox;
