import React, { useState } from 'react';
import "./App.css";

function DisplayTodoList({ todoList, onDeleteTodoItem, onAddTodoItem }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddTodoItem({ name, description, priority });
    setName('');
    setDescription('');
    setPriority('');
  };

  return (
    <div className="todo-app-container">
      <div className="create-task-container">
        <form onSubmit={handleSubmit} className="task-form">
          <div className="form-group task-name-input">
            <label>Add task Name: </label>
            <input
              type="text"
              className="form-control"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="form-group task-description-input">
            <label>Add task Description: </label>
            <input
              type="text"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>
          <div className="form-group task-priority-input">
            <label>Add task Priority(1-10): </label>
            <input
              type="number" // Changed to type number for proper input validation
              className="form-control"
              value={priority}
              onChange={(e) => setPriority(e.target.value)}
              min="1"
              max="10"
            />
          </div>
          <div className="submit-button-container">
            <input type="submit" value="Add task" className="btn btn-primary" />
          </div>
        </form>
      </div>
      <div className="todo-list-container">
        {todoList.length > 0 ? (
          <ul className="todo-list">
            {todoList.map((item) => (
              <li key={item.id} className="todo-item">
                <span>{item.name} - {item.description} (Priority: {item.priority})</span>
                <button
                  className="delete-button"
                  onClick={() => onDeleteTodoItem(item.id)}
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="no-tasks">No tasks found</p>
        )}
      </div>
    </div>
  );
}

export default DisplayTodoList;
