import React from 'react';
import "./App.css";

function DisplayTodoList({ todoList, onDeleteTodoItem }) {
  return (
    <div className="todo-app-container">
      <div className="todo-list-container">
        <h2>Daily Mood Description</h2>
        {todoList.length > 0 ? (
          <ul className="todo-list">
            {todoList.map((item) => (
              <li key={item.id} className="todo-item">
                <span className="todo-content">{item.content}</span>
                <button
                  className="delete-button"
                  onClick={() => onDeleteTodoItem(item.id)}
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="no-tasks">No tasks found</p>
        )}
        <button className="add-todo-button">Create a new todo item</button>
      </div>
    </div>
  );
}

export default DisplayTodoList;
